mapper.select:
select count(*) from db_company

  AND company_status != 0

    AND company_name = #{companyName,jdbcType=VARCHAR}


    AND company_id != #{companyId,jdbcType=VARCHAR}

SELECT u.company_id as companyId, u.company_name as companyName, u.company_english as companyEnglish, u.company_url as companyUrl, u.company_status as companyStatus, u.company_nature as companyNature, u.company_principal as companyPrincipal, u.company_tel as companyTel, u.company_mail as companyMail, u.established_time as establishedTime, u.company_address as companyAddress, u.company_remark as companyRemark
from db_company u
LIMIT #{start},#{limit}
